const messageService = require("../services/messageService");

const addMessage = async (req, res) => {
    let messageData = {
        user_id: req.user.id,
        name: req.body.name,
        mail: req.body.mail,
        content: req.body.content
    };

    try {
        await messageService.addMessage(messageData);
